sources.sass\\utils\\_commons.scss:
html {
  box-sizing: border-box;
}
*,
*::before,
*::after {
  box-sizing: inherit;
}

body {
  margin: 0;

  font-family: Open Sans, sans-serif;
  font-style: normal;
  font-weight: normal;
  font-size: 14px;
  line-height: calc(26 / 14);
  /* or 186% */
  letter-spacing: 0.02rem;
  color: $primary-text-color;
  background-color: #ffffff;
}
body.modal-open {
  overflow: hidden;
}
/* --------------------------------------------- common classes ------------------------------------------------------- */
.section {
  padding: 60px 0;

  @media screen and(min-width: $desktop) {
    padding: 94px 0;
  }
}

.container {
  padding-left: 30px;
  padding-right: 30px;
  margin-left: auto;
  margin-right: auto;

  @media screen and (min-width: $mobile) {
    width: $mobile;
    margin: 0 auto;
  }

  @media screen and (min-width: $tablet) {
    width: $tablet;
  }

  @media screen and (min-width: $desktop) {
    width: $desktop;
  }
}

.list {
  list-style: none;
}
h1,
h2,
h3,
h4,
h5,
h6,
p,
.item,
.link,
.text,
.img {
  padding: 0;
  margin: 0;
}

img {
  max-width: 100%;
  height: auto;
}

.section-title {
  margin-bottom: 30px;
  margin-top: 0;
  color: $text-title-color;
  font-weight: bold;
  font-size: 28px;
  line-height: calc(33 / 28);
  text-align: center;
  letter-spacing: 0.03rem;

  @media screen and (min-width: $desktop) {
    margin-bottom: 50px;
    font-size: 36px;
    line-height: 1.17;
  }
}

a {
  text-decoration: none;
}

ul {
  list-style: none;
  margin: 0px;
  padding: 0px;
}

