header {
  //.  должен быть как секция  -  центрироваться и сжиматься
  position: relative;

  box-sizing: border-box;
  max-width: $mobile; //| отзывчивость
  height: 580px;
  margin: 0 auto; //, выравнивает  по горизонтали
  padding: 0;

  background-color: $background-color;

  content: ' ';
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;

  background-image: linear-gradient(
      to right,
      rgba(25, 28, 38, 0.2),
      rgba(25, 28, 38, 0.2)
    ),
    url(../images/header/mobile-slider-img1-480x580.jpg);

  @media screen and (min-device-pixel-ratio: 2),
    screen and (min-resolution: 192dpi),
    screen and (min-resolution: 2dppx) {
    background-image: linear-gradient(
        to right,
        rgba(25, 28, 38, 0.2),
        rgba(25, 28, 38, 0.2)
      ),
      url(../images/header/mobile-slider-img1-960x1160.jpg);
  }

  @media screen and (min-width: $tablet) {
    //| - 2-я точка перелома_____________________________________________________________________
    max-width: $tablet; //| адаптивность
    height: 680px;

    background-color: $background-color;
    content: ' ';
    background-repeat: no-repeat;
    background-position: top right;
    background-size: contain;

    background-size: 609px 680px;
    background-image: linear-gradient(
        to right,
        rgba(25, 28, 38, 0.2),
        rgba(25, 28, 38, 0.2)
      ),
      url(../images/header/tablet-slider-img1-609x680.jpg);

    @media screen and (min-device-pixel-ratio: 2),
      screen and (min-resolution: 192dpi),
      screen and (min-resolution: 2dppx) {
      background-image: linear-gradient(
          to right,
          rgba(25, 28, 38, 0.2),
          rgba(25, 28, 38, 0.2)
        ),
        url(../images/header/tablet-slider-img1-1218x1360.jpg);
    }
  }
  @media screen and(min-width:$desktop) {
    //| 3-я точка перелома __________________________________________________________
    max-width: $desktop;

    content: ' ';
    background-repeat: no-repeat;
    background-position: top right;
    background-size: contain;
    background-size: 1125px 680px;
    background-image: linear-gradient(
        to right,
        rgba(25, 28, 38, 0.2),
        rgba(25, 28, 38, 0.2)
      ),
      url(../images/header/desktop-slider-img1-1125x680.jpg);

    @media screen and (min-device-pixel-ratio: 2),
      screen and (min-resolution: 192dpi),
      screen and (min-resolution: 2dppx) {
      background-image: linear-gradient(
          to right,
          rgba(25, 28, 38, 0.2),
          rgba(25, 28, 38, 0.2)
        ),
        url(../images/header/desktop-slider-img1-2250x1360.jpg);
    }
  }
}
.header-content {
  //. основное (все) содержание  хедера с лого и кнопками меню

  display: flex;
  padding: 40px 31px 0;
  margin: 0;
  width: 100%;
  justify-content: space-between;

  @media screen and (min-width: $tablet) {
    padding: 32px 0 0 50px;
    justify-content: start;
  }
}

.header-backdrop {
  position: fixed;
  top: 0;
  left: 0;

  display: none;
  width: 100%;
  height: 100%;
  background-color: rgba(25, 28, 38, 0.2);
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.04), 0px 2px 4px rgba(0, 0, 0, 0.08),
    0px 1px 3px rgba(0, 0, 0, 0.16);
}

.header-nav {
  //. nav - плавающая навигация  хедера (навигация, контакты и подписка , соцсети)

  margin-left: 60px;
  background-color: transparent;
  z-index: 50;
  font-weight: 600;
  font-size: 12px;
  line-height: calc(16 / 12);
  letter-spacing: 0.1rem;
  color: $background-color;

  @media screen and (max-width: 767px) {
    display: none;

    //, расставляет меню для моб.
    position: absolute;
    right: 0;
    top: 0;
    height: 100vh;
    width: 70%;
    margin-left: 103px;
    overflow: scroll;
    scroll-behavior: smooth;

    font-weight: 600;
    font-size: 18px;
    line-height: calc(25 / 18);

    text-align: start;
    letter-spacing: 0.1rem;

    color: $header-title-color;

    .hd-menu-container {
      height: 100%;
      margin: 0;
      padding-top: 118px;
      padding-left: 40px;
      background-color: #fff;

      .site-navigation {
        //. ul - навигация по сайту  , часть  флекса
        margin: 0 0 64px;

        padding: 0;

        .item:not(:last-child) {
          margin-bottom: 24px;
        }
        .item:hover,
        .item:focus {
          color: $accent-color;
        }
      }

      .header-contacts {
        //. ul - контакты и кнопка подписки
        padding: 0;
        margin-bottom: 64px;

        .item:not(:last-child) {
          margin-bottom: 24px;

          &:hover,
          &:focus {
            color: $accent-color;
          }
        }
      }
      //
      .header-social-links {
        //. ul - соцсети
        padding: 0;
        font-size: 14px;
        line-height: calc(19 / 14);
        letter-spacing: 0.2rem;

        .item {
          display: flex;
          align-items: center;
          margin-bottom: 24px;

          &::before {
            content: '';
            display: block;

            width: 60px;
            height: 1px;

            background: $header-title-color;

            margin-right: 40px;
          }
          &:last-child::before {
            visibility: hidden;
          }
        }
      }
    }
  }

  @media screen and (min-width: $tablet) {
    .hd-menu-container {
      display: flex;
      justify-content: start;
      align-items: center;
      background-color: transparent;
      font-weight: 600;
      font-size: 12px;
      line-height: calc(16 / 12);
      letter-spacing: 0.07rem;
      color: $background-color;

      .site-navigation {
        //. ul - навигация по сайту  , часть  флекса
        display: flex;
        justify-content: start;
        padding: 0;

        margin: 0 0 35px 0;

        //. отступы для элементов навигации
        .item:not(:last-child) {
          margin-right: 24px;
        }

        //. ховер для элементов навигации
        .item:hover,
        .item:focus {
          color: $accent-color;
          cursor: pointer;
        }
      }
      .header-contacts {
        //. ul - контакты и кнопка подписки
        display: flex;
        flex-direction: column-reverse;
        align-items: center;
        padding: 0;
        // margin: 0 0 0 40px;
        margin: 0 0 0 47px;
        //. отступы для элементов контактов (+кнопка подписки)
        .item:not(:last-child) {
          margin-top: 20px;

          padding: 0;

          //. ховет для элементов контактов (+ кнопка подписки)
          &:hover,
          &:focus {
            color: $accent-color;
            cursor: pointer;
          }
        }
      }

      .header-social-links {
        //. ul - соцсети

        //. позиционирование списка соцсетей
        position: absolute;
        bottom: 0;
        left: 89px;
        transform: rotate(-90deg);
        transform-origin: left;

        display: flex;
        padding: 0;
        font-size: 11px;
        line-height: calc(15 / 11);
        letter-spacing: 0.2rem;

        color: $header-title-color;

        .item {
          //. элемент соцсетей
          display: flex;
          align-items: center;
          margin: 0;

          //. псевдоэлемент соцсетей
          &::before {
            content: '';
            display: block;
            width: 60px;
            height: 1px;
            background-color: $header-title-color;
            margin: 0 40px;
          }

          //. отступы для элементов соцсетей
          &:last-child {
            margin-left: 40px;
          }

          &:last-child::before {
            display: none;
          }
        }
      }
    }
  }

  @media screen and (min-width: $desktop) {
    margin-left: 71px;
    .hd-menu-container {
      .site-navigation {
        //. ul - навигация по сайту  , часть  флекса
        display: flex;

        justify-content: start;
        padding: 0;
        margin: 0;

        .item:not(:last-child) {
          margin-right: 40px;
        }
      }
      .header-contacts {
        //. ul - контакты и кнопка подписки
        flex-direction: row;
        // align-items: center;
        padding-left: 322px;
        margin: 0;
        // margin: 0 0 0 300px;

        .item:not(:last-child) {
          margin: 0 40px 0 0;

          padding: 0;
        }
      }
    }
  }
}

.list {
  list-style: none;
}

.slider-nav {
  position: absolute;
  bottom: 49px;
  right: 55px;
}

.is-open {
  display: block;
}

.header-links {
  //. ссылка на соцсети (цвет/ ховер)
  text-decoration: none;
  color: $header-title-color;

  &:hover,
  &:focus {
    color: $accent-color;
  }
}
.revers {
  color: $background-color;
  @media screen and(max-width:767px) {
    color: $header-title-color;
  }
}

